import { useRef, useEffect, useState } from "react";
import SignaturePad from "signature_pad";

export default (props) => {
    const el = useRef(null);
    const [pad, setPad] = useState(null);

    useEffect(() => {
        setPad(new SignaturePad(el.current));
        pad.on("end", props.onChange(pad.toDataURL("image/svg+xml")));
    }, []);

    return <div style={{ width: 304, margin: "auto" }}>
        <div style={{ backgroundColor: "#eee" }}>
            <canvas style={{ border: "2px solid #ccc" }} ref={el}></canvas>
        </div>
        <button onClick={() => pad.clear()}>clear</button>
    </div>;
}
