import { Container, Row, Col } from 'react-grid-system';


export default () => {
    return <div>
        <style global jsx>{`
        body {
          margin: 0;
          padding: 0;
          background: #FCF9E9;
          -moz-osx-font-smoothing: grayscale;
          -webkit-font-smoothing: antialiased;
          -webkit-backface-visibility: hidden;
          font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
        }
        a{
            color: black;
        }
        .menu_items a{
            margin-left: 13px;
        }
        p{
            font-size: 18px;
            line-height: 24px;
            margin: 0;
            padding: 0;
            margin-bottom: 13px;
        }
        img.responsive{
            width: 100%;
        }
        .mb-1{
            margin-bottom: 13px;
        }
        .mb-2{
            margin-bottom: 26px;
        }
        .mb-3{
            margin-bottom: 39px;
        }
        .mb-4{
            margin-bottom: 52px;
        }
        .center{
            text-align: center;
        }
        .footer{
            margin-top: 52px;
            font-size: 13px;
            text-align: center;
        }
        button{
            color: #000;
            font-size: 18px;
            font-weight: bold;
            background-color: #F8D57E;
            border-radius: 23px;
            padding: 10px 15px;
        }
      `}</style>
        <div style={{ height: 70 }}>
            <Container className="mb-2">
                <Row>
                    <Col xs={4} style={{ lineHeight: "70px" }}><img src="/logo.svg" /></Col>
                    <Col className="menu_items" xs={8} style={{ textAlign: "right", lineHeight: "70px" }}>
                        <a href="#">Our mission</a>
                        <a href="#">Terms</a>
                    </Col>
                </Row>
            </Container>
            <Container>
                <div className="center mb-4">
                    <p style={{ margin: "auto", maxWidth: 670, fontWeight: "bold", fontSize: 39, lineHeight: "50px" }}>We are here to help you find, fill and sign covid-19 related forms.</p>
                    <div className="mb-2">
                        <button>Hi</button>
                    </div>
                </div>
                <div className="center">
                    <img className="responsive" style={{ minWidth: 270, maxWidth: 770 }} src="/cleaning.svg" />
                </div>
            </Container>
            <Container>
                <h1>The CoronaForms Mission</h1>
                <p>
                    <strong>CoronaForms</strong> mission to use technology to make coronavirus reliefs more accessable for everyone.
                </p>
                <p>We have seen the significant economic impact of the coronavirus on many people, typically the least well-paid and those self-employed or working in informal environments.</p>
                <p>Some governments and banks have announced economic measures to help with loans, safeguard jobs, guarantee wages and support the self-employed, but there is a lack of clarity in many countries about eligibility and the necessary paperwork behind it.</p>
            </Container>
            <Container className="footer">
                CoronaForms.org {new Date().getFullYear()} - Made with ❤️ in Israel
            </Container>
        </div>
    </div>
}
