import { Fragment } from "react";

function Button(props) {
    return <Fragment>
        <style jsx>{`
        button{
            color: #000;
            border: none;
            outline: none;
            cursor: pointer;
            font-size: 18px;
            font-weight: bold;
            padding: 10px 20px;
            position: relative;
            border-radius: 23px;
            transition: .15s ease;
            transition-property: all;
            background-color: #F8D57E;
        }
        button:disabled{
            opacity: 0.8;
            background: #e2e2e2;
            cursor: not-allowed;
        }
        button:hover{ transform: scale(1.1); }
        button:active{ transform: scale(0.98); }
        button .tooltip{
            left: 26%;
            color: #fff;
            padding: 6px;
            display: none;
            bottom: -30px;
            font-size:13px;
            background: #000;
            position: absolute;
            border-radius: 3px;
        }
        button:hover .tooltip{ display: block; }
        button div.arrow-up {
            width: 0; 
            height: 0; 
            left: 43%;
            top: -25px;
            position: absolute;
            border-left: 5px solid transparent;
            border-right: 5px solid transparent;
            border-bottom: 5px solid black;
        }
        `}</style>
        <button disabled={props.disabled}>
            {props.children}
            {props.tooltip && <div className="tooltip">
                {props.tooltip}
                <div style={{ position: "relative" }}>
                    <div className="arrow-up"></div>
                </div>
            </div>}
        </button>
    </Fragment>
}

export default Button;
