import { useRef } from "react";
import StepField from "./StepField";
export default (props) => {
    const form = useRef(null);
    function handleSubmit(event) {
        event.preventDefault();
        props.onNavigate(form.payload);
        form.payload = null;
    }
    return <div>
        <h1 className="mb-2">{props.q.title}</h1>
        <h2 className="mb-2">{props.q.subtitle}</h2>
        <form ref={form} onSubmit={handleSubmit}>
            {props.q.fields.map((field, index) => <StepField
                key={index}
                value={props.data[field.name]}
                onUpdate={props.onUpdate}
                onNavigate={(ref) => form.payload = ref, form.submit()}
                field={field} />)}
        </form>
    </div>
}
