import "./style.css";
import Hero from "../components/Hero";
import Step from "../components/Step";
import Button from "../components/Button";
import Questions from "../questions.json";
import { useState, Fragment } from "react";
import { Container, Row, Col } from "react-grid-system";

import strings from "../strings.json";

export default () => {
    const [step, setStep] = useState("choose_bank");
    const [data, setData] = useState({});
    const [prevStep, setPrevStep] = useState([]);

    function handleUpdate(key, value) {
        let newData = { ...data }
        newData[key] = value;
        setData(newData);
    }

    function handleNavigation(ref) {
        if (typeof ref === "string") {
            setPrevStep([ref, ...prevStep]);
            setStep(ref);
            return;
        }

        if (data[ref["if"][0]] == ref["if"][2]) {
            handleNavigation(ref["if"][3]);
            return;
        }

        handleNavigation(ref["else"]);
    }

    function handleBack(steps) {
        setStep(steps[0]);
        let prevStepClone = [...prevStep];
        prevStepClone.shift();
        setStep(prevStepClone);
    }

    return <div className="english">
        <Container>
            <Row className="header">
                <Col xs={4}><img src="/logo.svg" /></Col>
                <Col xs={8} className="menuItems">
                    <a href="#mission">{strings.english.Header.OurMission}</a>
                    <a href="#terms-of-use">{strings.english.Header.Terms}</a>
                </Col>
            </Row>
            <div className="text-center mt-2">
                <Hero>
                    {Questions[step] && <Step data={data} onUpdate={handleUpdate} onNavigate={handleNavigation} q={Questions[step]} />}
                    {prevStep.length > 0 && <Button onClick={handleBack}>Back</Button>}
                </Hero>
            </div>
            <div id="mission" className="mission">
                <h3>{strings.english.OurMission.title}</h3>
                <p>{strings.english.OurMission.p1}</p>
                <p>{strings.english.OurMission.p2}</p>
                <p><strong>CoronaForms</strong> {strings.english.OurMission.p3}</p>
                <div className="text-right mt-3">
                    <img src="/coronaforms.svg" />
                </div>
            </div>
        </Container>
        <div className="footer">
            <div className="floor"></div>
            <Container>
                <div className="mb-1 pt-1">
                    CoronaForms.org {new Date().getFullYear()}
                </div>
                <div className="mb-1">{strings.english.Footer.MadeWithLoveInIsrael}</div>
            </Container>
            <div style={{ background: "#000", height: 20 }}></div>
        </div>
    </div>
}
