import strings from "../strings.json";
import Button from "../components/Button";
import { useState, Fragment } from "react";
import { Row, Col } from "react-grid-system";

export default (props) => {
    const [step, setStep] = useState(0);
    const [borrowers, setBorrowers] = useState([1]);

    function addBorrower() {
        const newBorrowers = [...borrowers, borrowers[borrowers.length - 1] + 1];
        setBorrowers([]);
        setTimeout(() => {
            setBorrowers(newBorrowers);
        }, 0);
    }

    function removeBorrower(id) {
        let clonedBorrowers = [...borrowers].filter(_id => _id !== id);
        setBorrowers([]);
        setTimeout(() => {
            setBorrowers(clonedBorrowers);
            setTimeout(() => {
                props.handleUpdate(`id_number_${id}`, "");
                props.handleUpdate(`first_name_${id}`, "");
            }, 0)
        }, 0);
    }

    return <Fragment>
        {step === 0 && <Fragment>
            <h1 className="mb-2">Who's the mortgage registered to?</h1>
            <h2 className="mb-2">It's important to add all people the mortgage is registered to.</h2>
            {
                borrowers.map((id) => <div className="relative">
                    <Row className="mb-2">
                        <Col sm={6}>
                            <input defaultValue={props.data[`first_name_${id}`]} onChange={event => props.handleUpdate(`first_name_${id}`, event.target.value)} className="field not-round" placeholder={strings.english.Common.FullName} />
                        </Col>
                        <Col sm={6}>
                            <input defaultValue={props.data[`id_number_${id}`]} onChange={event => props.handleUpdate(`id_number_${id}`, event.target.value)} className="field not-round" placeholder={strings.english.Common.IDNumber} />
                        </Col>
                    </Row>
                    {id > 1 && <Button onClick={() => removeBorrower(id)}>Remove</Button>}
                </div>)
            }
            <div className="text-right mt-2">
                <Button onClick={addBorrower}>Add</Button>
            </div>
        </Fragment>}
        {step === 1 && <Fragment>

        </Fragment>}
    </Fragment>
}
