import strings from "../strings.json";
import Button from "../components/Button";
import { useState, Fragment } from "react";
import { Row, Col } from "react-grid-system";

export default (props) => {
    const [step, setStep] = useState(0);
    const [borrowers, setBorrowers] = useState([1]);

    function addBorrower() {
        setBorrowers([...borrowers, borrowers[borrowers.length - 1] + 1]);
    }

    return <Fragment>
        {step === 0 && <Fragment>
            <h1 className="mb-2">Who's the mortgage registered to?</h1>
            <h2 className="mb-2">It's important to add all people the mortgage is registered to.</h2>
            {
                borrowers.map((id) => <Fragment>
                    <Row className="mb-2">
                        <Col sm={6}>
                            <input onChange={props.handleUpdate()} className="field not-round" placeholder={strings.english.Common.FullName} />
                        </Col>
                        <Col sm={6}>
                            <input className="field not-round" placeholder={strings.english.Common.IDNumber} />
                        </Col>
                    </Row>
                </Fragment>)
            }
            <div className="text-right mt-2">
                <Button onClick={addBorrower}>Add</Button>
            </div>
        </Fragment>}
        {step === 1 && <Fragment>

        </Fragment>}
    </Fragment>
}
