import strings from "../strings.json";
import Button from "../components/Button";
import { useState, Fragment } from "react";
import { Row, Col } from "react-grid-system";

export default (props) => {
    const [step, setStep] = useState(0);
    const [firstName1, setFirstName1] = useState("");
    const [firstName2, setFirstName2] = useState("");
    const [firstName3, setFirstName3] = useState("");

    const [IDNumber1, setIDNumber1] = useState("");
    const [IDNumber2, setIDNumber2] = useState("");
    const [IDNumber3, setIDNumber3] = useState("");

    const [visible, setVisible] = useState(0);

    return <Fragment>
        {step === 0 && <Fragment>
            <h1 className="mb-2">Is this a sole or joint account?</h1>
            <h2 className="mb-2">Make sure to add all the account owners.</h2>
            <input value={firstName1} onChange={e => setFirstName1(e.target.value)} className="field not-round" placeholder="First name" />
            <input value={IDNumber1} onChange={e => setIDNumber1(e.target.value)} className="field not-round" placeholder="ID number" />
            <Button arrow>Add</Button>
            <div className="mt-2">
                <input value={firstName2} onChange={e => setFirstName2(e.target.value)} className="field not-round" placeholder="First name" />
                <input value={IDNumber2} onChange={e => setIDNumber2(e.target.value)} className="field not-round" placeholder="ID number" />
            </div>
            <div className="mt-2 mb-2">
                <input value={firstName3} onChange={e => setFirstName3(e.target.value)} className="field not-round" placeholder="First name" />
                <input value={IDNumber3} onChange={e => setIDNumber3(e.target.value)} className="field not-round" placeholder="ID number" />
            </div>
            <Button arrow>Next</Button>
        </Fragment>}
        {step === 1 && <Fragment>

        </Fragment>}
    </Fragment>
}
