import Button from "./Button";
import { Fragment } from "react";

export default (props) => {

    function handleOptionClick(option) {
        props.onUpdate(props.field.name, option.value);
        props.onNavigate(option.ref);
    }

    return <Fragment>
        {props.field.type === "select" && <Fragment>
            {props.field.options.map(btn => <Button className="ml-2" arrow onClick={() => handleOptionClick(btn)}>{btn.text}</Button>)}
        </Fragment>}
        {props.field.type === "input" && <Fragment>
            <input className="field" onChange={e => props.onChange()} placeholder={props.field.placeholder} />
        </Fragment>}
        {props.field.type === "button" && <Fragment>
            <Button arrow>{props.field.text}</Button>
        </Fragment>}
    </Fragment>
}
