import styled from 'styled-components'

const RoundButton = styled.button`
    color: #000;
    font-size: 18px;
    font-weight: bold;
    background-color: #F8D57E;
    border-radius: 23px;
    padding: 10px 20px;
    border: none;
    cursor: pointer;
    outline: none;
    margin-left: 26px;
    transition: .15s ease;
    transition-property: all;
    position: relative;
`;

const Tooltip = styled.div`
    position: absolute;
    background: #000;
    color: #fff;
    border-radius: 3px;
    padding: 6px;
    font-size:13px;
    bottom: -35px;
    left: 26%;
    display: none;
`;

function Button(props) {
    return <RoundButton disabled={props.disabled}>
        {props.children}
        {props.tooltip && <div className="tooltip">{props.tooltip}</div>}
    </RoundButton>
}

export default Button;
