import { Fragment } from "react";
import strings from "../strings.json";
import Button from "../components/Button";
import NewsletterForm from "../components/NewsletterForm";
import { useState, useEffect } from "react";

function Hero() {
    const [country, setCountry] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchData() {
            const response = await fetch("http://localhost:8080/api/v1/geo");
            const jsonData = await response.json();
            setCountry(jsonData.countryName);
            setTimeout(() => {
                setLoading(false);
            }, 1000);
        }
        fetchData();
    }, []);

    return <div className="auto hero">
        <h1 className="mb-2">{strings.english.hero.title}</h1>
        <Fragment>
            {loading && <div className="loading"><img width="50" src="/puff.svg" /></div>}
            {!loading && country === "Israelx" && <Fragment>
                <h2 className="mb-3">{strings.english.hero.subtitle}: {country}</h2>
                <Button className="mr-2" disabled tooltip="Coming soon">{strings.english.hero.SmallBusinessAid}</Button>
                <Button arrow>{strings.english.hero.MortgageRelief}</Button>
            </Fragment>}
            {!loading && country !== "Israelx" && <Fragment>
                <h2 className="mb-3 text-left">{strings.english.hero.NoFormsFound}</h2>
                <NewsletterForm />
            </Fragment>}
        </Fragment>
    </div>
}

export default Hero;
