import { Fragment } from "react";
import strings from "../strings.json";
import Button from "../components/Button";
import { useState, useEffect } from "react";
import NewsletterForm from "../components/NewsletterForm";
import { Row, Col } from "react-grid-system";

function Hero() {
    const [country, setCountry] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchData() {
            const response = await fetch("http://localhost:8080/api/v1/geo");
            const jsonData = await response.json();
            setCountry(jsonData.countryName);
            setTimeout(() => {
                setLoading(false);
            }, 1000);
        }
        fetchData();
    }, []);

    return <div className="auto hero">
        <h1 className="mb-2">{strings.english.Hero.title}</h1>
        {loading && <div className="loading"><img width="50" src="/puff.svg" /></div>}
        {!loading && country === "Israel" && <Fragment>
            <h2 className="mb-2">{strings.english.Hero.subtitle}: <strong>{country}</strong></h2>
            <div style={{ maxWidth: 450, margin: "auto" }}>
                <Row>
                    <Col md={6}>
                        <Button className="mr-2" disabled tooltip={strings.english.Common.ComingSoon}>{strings.english.Hero.SmallBusinessAid}</Button>
                    </Col>
                    <Col md={6}>
                        <Button arrow>{strings.english.Hero.MortgageRelief}</Button>
                    </Col>
                </Row>
            </div>
        </Fragment>}
        {!loading && country !== "Israel" && <Fragment>
            <h2 className="mb-2 text-left">{strings.english.Hero.NoFormsFound}</h2>
            <NewsletterForm subtitle={strings.english.Common.PrivacyPolicy} />
        </Fragment>}
    </div>
}

export default Hero;
