import { useState, useRef, Fragment } from "react";

export default props => {
    const el = useRef(null);
    const [value, setValue] = useState("");
    const [focus, setFocus] = useState(false);

    function handleChange(event) {
        setValue(event.target.value);
        props.onChange(event);
    }

    function handleBlur() {
        if (value !== "") {
            return;
        }
        setFocus(false);
    }

    return <Fragment>
        <style jsx>{`
        .input-field{
            position: relative;
        }
        .input-field label{
            display: block;
            position: absolute;
            top: 19px;
            ${props.rtl ? "right" : "left"}: ${props.rtl ? "10" : "20"}px;
            font-size: 24px;
            color: #757575;
            transition: .15s ease;
            transition-property: all;
        }
        .input-field.focus label{
            top: 8px;
            font-size: 16px;
            font-weight: bold;
        }
        .input-field input{
            border: none;
            outline: none;
            width: 100%;
            padding: 20px;
            padding-top: 28px;
            padding-bottom: 10px;
            font-size: 24px;
            padding-right: 50px;
            transition: .15s ease;
            box-sizing: border-box;
            transition-property: all;
            border-top-left-radius: 10px;
            border-top-right-radius: 10px;
            border-bottom: 2px solid #ccc;
            text-align: ${props.rtl ? "right" : "left"};
            direction: ${props.rtl ? "rtl" : "ltr"};
        }
        `}</style>
        <div className={["input-field", focus ? "focus" : ""].join(" ")}>
            <label onClick={() => el.current.focus()}>{props.label || props.placeholder}</label>
            <input type={props.type || "text"} ref={el} value={value} onChange={handleChange} onFocus={() => setFocus(true)} onBlur={handleBlur} />
        </div>
    </Fragment>
}
