import { useState } from "react";

export default props => {
    const [focus, setFocus] = useState(false);

    return <div className={["input-field", focus ? "focus" : ""].join(" ")}>
        <label>{props.label}</label>
        <input onFocus={() => setFocus(true)} onBlur={() => setFocus(false)} {...props} />
    </div>
}
