import "./style.css";
import Hero from "../components/Hero";
import strings from "../strings.json";
import Button from "../components/Button";
import { useState, Fragment } from "react";
import LeumiForm from "../components/LeumiForm";
import { Container, Row, Col } from "react-grid-system";

export default () => {
    const [data, setData] = useState({});
    const [step, setStep] = useState("main");

    function handleUpdate(key, value, next = null) {
        let newData = { ...data };
        newData[key] = value;
        setData(newData);
        if (next) {
            setStep(next);
        }
    }

    return <div className="english">
        <Container>
            <Row className="header">
                <Col xs={4}><img src="/logo.svg" /></Col>
                <Col xs={8} className="menuItems">
                    <a href="#mission">{strings.english.Header.OurMission}</a>
                    <a href="#terms-of-use">{strings.english.Header.Terms}</a>
                </Col>
            </Row>
            <div className="text-center mt-2">
                <Hero>
                    {step === "main" && <Fragment>
                        <h1 className="mb-2">{strings.english.Hero.title}</h1>
                        <h2 className="mb-2">{strings.english.Hero.subtitle}</h2>
                        <Button disabled tooltip={strings.english.Common.ComingSoon} arrow>{strings.english.Hero.SmallBusinessAid}</Button>
                        <Button arrow onClick={() => setStep("choose_bank")}>{strings.english.Hero.MortgageSuspension}</Button>
                    </Fragment>}
                    {step === "choose_bank" && <Fragment>
                        <h1 className="mb-2">Choose your bank</h1>
                        <h2 className="mb-2">We need to know what is your bank to get the right form.</h2>
                        <Button onClick={() => handleUpdate("bank", "leumi", "bank_form")} arrow>Leumi Bank</Button>
                        <Button onClick={() => handleUpdate("bank", "discount", "bank_form")} arrow>Discount Bank</Button>
                        <Button onClick={() => handleUpdate("bank", "jerusalem", "bank_form")} arrow>Jerusalem Bank</Button>
                    </Fragment>}
                    {step === "bank_form" && <Fragment>
                        {data.bank === "leumi" && <LeumiForm />}
                    </Fragment>}
                </Hero>
            </div>
            <div id="mission" className="mission">
                <h3>{strings.english.OurMission.title}</h3>
                <p>{strings.english.OurMission.p1}</p>
                <p>{strings.english.OurMission.p2}</p>
                <p><strong>CoronaForms</strong> {strings.english.OurMission.p3}</p>
                <div className="text-right mt-3">
                    <img src="/coronaforms.svg" />
                </div>
            </div>
        </Container>
        <div className="footer">
            <div className="floor"></div>
            <Container>
                <div className="mb-1 pt-1">
                    CoronaForms.org {new Date().getFullYear()}
                </div>
                <div className="mb-1">{strings.english.Footer.MadeWithLoveInIsrael}</div>
            </Container>
            <div style={{ background: "#000", height: 20 }}></div>
        </div>
    </div>
}
