import { useState, useRef } from "react";

export default props => {
    const el = useRef(null);
    const [value, setValue] = useState("");
    const [focus, setFocus] = useState(false);

    function handleChange(event) {
        setValue(event.target.value);
        props.onChange(event);
    }

    function handleBlur() {
        if (value !== "") {
            return;
        }
        setFocus(false);
    }

    return <div className={["input-field", focus ? "focus" : ""].join(" ")}>
        <label onClick={() => el.current.focus()}>{props.label || props.placeholder}</label>
        <input type={props.type || "text"} ref={el} value={value} onChange={handleChange} onFocus={() => setFocus(true)} onBlur={handleBlur} />
    </div>
}
