import { useRef, useState, useEffect } from "react";
import StepField from "./StepField";
export default (props) => {
    const form = useRef(null);
    const [ref, setRef] = useState(null);

    function handleSubmit(event) {
        alert(10);
        event.preventDefault();
        props.onNavigate(ref);
        setRef(null);
    }

    function handleNavigationRequest(ref) {
        setRef(ref);
        //form.current.submit();
    }

    useEffect(() => {
        form.current.addEventListener("submit", (event) => {
            event.preventDefault();
        });
    }, []);

    return <div>
        <h1 className="mb-2">{props.q.title}</h1>
        <h2 className="mb-2">{props.q.subtitle}</h2>
        <form ref={form} onSubmit={handleSubmit}>
            {props.q.fields.map((field, index) => <StepField
                key={index}
                value={props.data[field.name]}
                onUpdate={props.onUpdate}
                onNavigate={handleNavigationRequest}
                field={field} />)}
        </form>
    </div>
}
