import Button from "./Button";
import { Fragment, useEffect, useRef } from "react";

export default (props) => {
    const el = useRef(null);

    function handleOptionClick(option) {
        props.onUpdate(option.name || props.field.name, option.value);
        props.onNavigate(option.ref);
    }

    function handleInputChange(event) {
        props.onUpdate(props.field.name, event.target.value);
    }

    useEffect(() => {
        if (el.current) {
            el.current.value = props.value;
        }
    }, []);

    return <Fragment>
        {props.field.type === "select" && <Fragment>
            {props.field.options.map((btn, index) => <Button key={index} disabled={btn.disabled} className="ml-2" arrow onClick={() => handleOptionClick(btn)}>{btn.text}</Button>)}
        </Fragment>}
        {props.field.type === "input" && <Fragment>
            <input type={props.field.field_type || "text"} ref={el} defaultValue={props.value} className="field" onChange={handleInputChange} placeholder={props.field.placeholder} />
        </Fragment>}
        {props.field.type === "button" && <Fragment>
            <Button disabled={props.field.disabled} onClick={() => props.onNavigate(props.field.ref)} arrow>{props.field.text}</Button>
        </Fragment>}
    </Fragment>
}
