import Button from "./Button";
import { Fragment } from "react";

export default (props) => {
    return <Fragment>
        {props.field.type === "select" && <Fragment>
            {props.field.options.map(btn => <Button className="ml-2" arrow onClick={() => props.onClick(btn.ref)}>{btn.text}</Button>)}
        </Fragment>}
        {props.field.type === "input" && <Fragment>
            <input placeholder={props.field.placeholder} />
        </Fragment>}
    </Fragment>
}
