import strings from "../strings.json";
import Button from "../components/Button";
import { useState, Fragment } from "react";
import { Row, Col } from "react-grid-system";

export default (props) => {
    const [step, setStep] = useState(0);
    const [borrowers, setBorrowers] = useState([1]);

    function addBorrower() {
        setBorrowers([...borrowers, borrowers[borrowers.length - 1] + 1]);
    }

    function removeBorrower(id) {
        let clonedBorrowers = [...borrowers].filter(_id => _id !== id);
        setBorrowers(clonedBorrowers);
    }

    return <Fragment>
        {step === 0 && <Fragment>
            <h1 className="mb-2">Who's the mortgage registered to?</h1>
            <h2 className="mb-2">It's important to add all people the mortgage is registered to.</h2>
            {
                borrowers.map((id) => <div className="relative">
                    <Row className="mb-2">
                        <Col sm={6}>
                            <input onChange={event => props.handleUpdate(`first_name_${id}`, event.target.value)} className="field not-round" placeholder={strings.english.Common.FullName} />
                        </Col>
                        <Col sm={6}>
                            <input onChange={event => props.handleUpdate(`id_number_${id}`, event.target.value)} className="field not-round" placeholder={strings.english.Common.IDNumber} />
                        </Col>
                    </Row>
                    <Button onClick={addBorrower}>Add</Button>
                </div>)
            }
            <div className="text-right mt-2">
                <Button onClick={addBorrower}>Add</Button>
            </div>
        </Fragment>}
        {step === 1 && <Fragment>

        </Fragment>}
    </Fragment>
}
