import { Fragment } from "react";

export default (props) => {
    return <Fragment>
        <style jsx>{`
            .relative{
                position: relative;
            }
            .relative input{
                border: none;
                width: 100%;
                outline: none;
                font-size: 24px;
                padding: 10px 20px;
            }
        `}</style>
        <div className="relative">
            <label>{props.label}</label>
            <input required value={props.value} onChange={props.onChange} />
        </div>
    </Fragment>
}
