import Button from "./Button";
import { Fragment } from "react";

export default (props) => {

    function handleOptionClick(option) {
        props.onUpdate(props.field.name, option.value);
        props.onNavigate(option.ref);
    }

    function handleInputChange(event) {
        props.onUpdate(props.field.name, event.target.value);
    }

    return <Fragment>
        {props.field.type === "select" && <Fragment>
            {props.field.options.map(btn => <Button disabled={btn.disabled} className="ml-2" arrow onClick={() => handleOptionClick(btn)}>{btn.text}</Button>)}
        </Fragment>}
        {props.field.type === "input" && <Fragment>
            <input type={props.field.field_type || "text"} defaultValue={props.value} className="field" onChange={handleInputChange} placeholder={props.field.placeholder} />
        </Fragment>}
        {props.field.type === "button" && <Fragment>
            <Button disabled={props.field.disabled} onClick={() => props.onNavigate(props.field.ref)} arrow>{props.field.text}</Button>
        </Fragment>}
    </Fragment>
}
